	function removeActiveClass(elements){
		elements.removeClass('active');

		$('.map', '.section-bottom-map').removeClass('active');
		$('.circle', '.section-bottom-map').removeClass('active');
	}

		$('p','#active-elements').mouseenter(function (e) {

					var current = elements.index(e.target);

				removeActiveClass(elements);

				$(e.target).addClass('active');

				switch (current) {
					case 0:
						$('.map2', '.section-bottom-map').addClass('active');
						$('.circle5', '.section-bottom-map').addClass('active');


						break;
					case 1:
						$('.map3', '.section-bottom-map').addClass('active');
						$('.circle4', '.section-bottom-map').addClass('active');
						break;
					case 2:
						$('.map1', '.section-bottom-map').addClass('active');
						$('.circle2', '.section-bottom-map').addClass('active');

						break;
					case 3:
						$('.map4', '.section-bottom-map').addClass('active');
						$('.circle3', '.section-bottom-map').addClass('active');
						break;
				}
	});

	var mapParent = '.section-bottom-map';

	$('.map','#Layer_1').mouseenter(function (e) {

			removeActiveClass(elements);

			if($(e.target).hasClass('map1')) {
				$('.map1', mapParent).addClass('active');
				$('.circle2', mapParent).addClass('active');
				elements.eq(2).addClass('active');
			}
			if($(e.target).hasClass('map2')) {
				$('.map2', mapParent).addClass('active');
				$('.circle5', mapParent).addClass('active');
				elements.eq(0).addClass('active');
			}
			if($(e.target).hasClass('map3')) {
				$('.map3', mapParent).addClass('active');
				$('.circle4', mapParent).addClass('active');
				elements.eq(1).addClass('active');
			}
			if($(e.target).hasClass('map4')) {
				$('.map4', mapParent).addClass('active');
				$('.circle3', mapParent).addClass('active');
				elements.eq(3).addClass('active');
			}

		$('#Layer_1').click(function () {
			$('p.active', '.active-elements').click();
		})

	});

	$('#mainSlider').on('afterChange', function(){
		var parent = '#sliderMap';

		$('.map', parent).removeClass('active');

		switch ($('.slick-active').index()) {
			case 0:
				$('.map1', parent).addClass('active');
				$(parent).css({
					'-webkit-transform' : 'translate(0px, -54%) scale(1.3)',
					'-moz-transform'    : 'translate(0px, -54%) scale(1.3)',
					'-ms-transform'     : 'translate(0px, -54%) scale(1.3)',
					'-o-transform'      : 'translate(0px, -54%) scale(1.3)',
					'transform'            : 'translate(0px, -54%) scale(1.3)'});
				break;

			case 1:
				$('.map2', parent).addClass('active');
				$(parent).css({
					'-webkit-transform' : 'translate(0,-10%)',
					'-moz-transform'    : 'translate(0,-10%)',
					'-ms-transform'     : 'translate(0,-10%)',
					'-o-transform'      : 'translate(0,-10%)',
					'transform'            : 'translate(0,-10%)'});
				break;

			case 2:
				$('.map3', parent).addClass('active');
				$(parent).css({
					'-webkit-transform' : 'translate(0px, -38%) scale(1.3)',
					'-moz-transform'    : 'translate(0px, -38%) scale(1.3)',
					'-ms-transform'     : 'translate(0px, -38%) scale(1.3)',
					'-o-transform'      : 'translate(0px, -38%) scale(1.3)',
					'transform'            : 'translate(0px, -38%) scale(1.3)'});

				break;

			case 3:
				$('.map4', parent).addClass('active');
				$(parent).css({
					'-webkit-transform' : 'translate(0,-40%)',
					'-moz-transform'    : 'translate(0,-40%)',
					'-ms-transform'     : 'translate(0,-40%)',
					'-o-transform'      : 'translate(0,-40%)',
					'transform'            : 'translate(0,-40%)'});
				break;
		}
	});

